  const valid = await resetSchema.isValid(data.body)
  if (valid) {
    await setLocale(data)
    const u = await user(data)
      .catch(() => return await sendErr(400, t('error.required'), done))
    if (u.email) {
      const userData = await db.read('users', u.email).catch(() => return await sendErr(400, t('error.no_user'), done))
      if (userData) {
        await sendReset(u.email, userData.phone).catch(() => return await sendErr(500, t('error.email'), done))
        return { status: 200, out: t('ok') }
      }
      return await sendErr(400, t('error.no_user'), done)
    }
    return await sendErr(400, t('error.required'), done)
  }
  return await sendErr(400, t('error.required'), done)
}

