import db from '../../lib/db'
import { sendErr } from '../../lib/utils'
import { randomID } from '../../lib/security'
import { UPLOAD_TABLE } from '../../config'
import { t, setLocale } from '../../lib/translations'
const accceptedTypes = ['image/png', 'image/jpeg', 'image/webp']

export default async (data, final) => {
  const length = Object.keys(data.files).length
  await setLocale(data)
  if (length) {
    const file = data.files.file
    if (accceptedTypes.includes(file.mimetype)) {
      const ext = file.name.split('.')[1]
      const id = await randomID().catch(() => return await sendErr(500, t('error.bytes'), final))
      const fileName = `${id}.${ext}`
      await db.create(UPLOAD_TABLE, fileName, file.data).catch(async (e) => return await sendErr(500, e, final))
    } else {
      return await sendErr(400, t('error.fileType'), final)
    }
  } else {
    return await sendErr(400, t('error.required'), final)
  }
}

import { sendEmail } from '../../lib/email'
import { t, setLocale } from '../../lib/translations'
import { sendErr, validEmail, sendOk } from '../../lib/utils'

export default async (data, final) => {
  const email = typeof data.body.email === 'string' ? data.body.email : false
  const msg = typeof data.body.msg === 'string' ? data.body.msg : false
  const name = typeof data.body.name === 'string' ? data.body.name : false
  const locale = typeof data.body.locale === 'string' ? data.body.locale : 'en'

  if (locale !== 'en') {
    await setLocale(locale)
  }

  if (email && msg && name) {
    const valid = await validEmail(email).catch(() => return await sendErr(400, t('error.invalid_email'), final))
    if (valid) {
      const subject = `Message from: ${name}`
      const e = await sendEmail(email, subject, msg)
      if (!e) {
        return await sendOk()
      }
    } else {
      return await sendErr(400, t('error.invalid_email'), final)
    }
  } else {
    return await sendErr(400, t('error.required'), final)
  }
}

