import { resolve } from 'path'
import protobuf from 'protobufjs'
import { promisify } from 'util'

import { ALLOW_ORIGIN } from '../../config'
import { sendErr } from '../utils'
import { t } from '../translations'
const definitionsDirectory = resolve(__dirname, '../schemas')
const resBundle = resolve(definitionsDirectory, 'resBundle.json')
const reqBundle = resolve(definitionsDirectory, 'reqBundle.json')

const decoder = (p, base64Buffer, messageType, done) => {
  protobuf.load(p, (err, root) => {
    if (!err && root) {
      try {
        const MsgType = root.lookupType(messageType)
        const message = MsgType.decode(Buffer.from(base64Buffer, 'base64'))
        const obj = MsgType.toObject(message)
        done(null, obj)
      } catch (e) {
        done(e.message)
      }
    } else if (err) {
      done(err.message)
    }
  })
}

const _decode = async (base64Buffer, messageType, done) => {
  decoder(reqBundle, base64Buffer, messageType, done)
}

export const decode = promisify(_decode)

const encoder = (p, output, messageType, done) => {
  protobuf.load(p, (err, root) => {
    if (!err && root) {
      try {
        const MsgType = root.lookupType(messageType)
        const message = MsgType.create(output)
        const enc = MsgType.encode(message).finish()
        done(null, enc)
      } catch (e) {
        done(e.message)
      }
    } else {
      done(err.message)
    }
  })
}

const _encode = (output, messageType, done) => {
  encoder(resBundle, output, messageType, done)
}

export const encode = promisify(_encode)

// @TODO remove messageType
const _encodeRequest = async (output, messageType, done) => {
  _encodeBody(output, done)
}

export const encodeRequest = promisify(_encodeRequest)

const _decodeResponse = (base64Buffer, messageType, done) => {
  decoder(resBundle, base64Buffer, messageType, done)
}

export const decodeResponse = promisify(_decodeResponse)

const _encodeBody = (base64Buffer, done) => {
  encoder(resBundle, base64Buffer, 'Body', done)
}

export const encodeBody = promisify(_encodeBody)

const _decodeBody = (base64Buffer, done) => {
  decoder(reqBundle, base64Buffer, 'Body', done)
}

export const decodeBody = promisify(_decodeBody)

const _protoResponse = async (statusCode, output, messageType, done) => {
  const buffer = await encode(output, messageType).catch(async (e) => done(null, await sendErr(500, e)))
  if (buffer) {
    const body = buffer.toString('base64')
    done(null, {
      statusCode,
      headers: {
        'Content-Type': 'application/x-protobuf',
        Accept: 'application/x-protobuf',
        'Access-Control-Allow-Origin': ALLOW_ORIGIN,
        Action: messageType
      },
      body,
      isBase64Encoded: true
    })
  } else {
    done(null, await sendErr(500, t('error.unknown')))
  }
}

export const protoResponse = promisify(_protoResponse)

import http from 'http'
import https from 'https'
import { stringify } from 'querystring'
import legit from 'legit'
import { promisify } from 'util'
import { validate } from 'isemail'

import { protoResponse, decode, decodeBody } from '../proto'
import db from '../db'
import { t } from '../translations'
import { apiAuth } from '../auth'
import { handlers } from '../../handlers'

export const sendErr = async (status, msg) => {
  return await protoResponse(status, { error: msg }, 'Error')
}

export const sendOk = async () => {
  return await protoResponse(200, { status: 'Ok' }, 'Ok')
}

export const _request = (schema, obj, done) => {
  const schemaLib = typeof schema === 'string' && schema === 'http' ? http : https
  const payloadString = typeof obj.data === 'object' ? stringify(obj.data) : false
  if (payloadString && typeof obj.headers === 'object') {
    obj.headers['Content-Length'] = Buffer.byteLength(payloadString)
    const req = schemaLib.request(obj, (res) => {
      const status = res.statusCode
      if (status === 200 || status === 201) {
        done()
      } else {
        done(`Response status: ${status}`)
      }
    })

    req.on('error', (err) => {
      done(err.message)
    })

    req.on('timeout', () => {
      done(t('error.timeout'))
    })

    req.write(payloadString)
    req.end()
  } else {
    done(t('error.required'))
  }
}

export const request = promisify(_request)

export const finalizeRequest = (collection, id, action, obj, final) => {
  if (typeof obj === 'object') {
    db[action](collection, id, obj, async (err, data) => {
      if (!err) {
        final(null, { s: 200, o: { status: 'ok' } })
      } else {
        final(null, { s: 500, e: t('error.internal') })
      }
    })
  } else {
    db[action](collection, id, async (err, data) => {
      if (!err) {
        final(null, { s: 200, o: { status: 'ok' } })
      } else {
        final(null, { s: 500, e: t('error.internal') })
      }
    })
  }
}

const _validEmail = async (email, done) => {
  if (validate(email)) {
    const r = await legit(email).catch(() => done(null, false))
    if (r && r.isValid) {
      done(null, email)
    } else {
      done(null, false)
    }
  } else {
    done(null, false)
  }
}

export const validEmail = promisify(_validEmail)

const _decodeRequest = async (event, done) => {
  const decodedEvent = await decodeBody(event).catch((e) => done(e))
  const authorized = await apiAuth(decodedEvent).catch((e) => done(e))
  if (authorized) {
    const obj = {
      body: authorized.body,
      headers: authorized.headers
    }
    done(null, obj)
  } else {
    done(t('error.unauthorized'))
  }
}

export const decodeRequest = promisify(_decodeRequest)

const _response = async (event, done) => {
  const payload = await decodeRequest(event).catch(async (e) => done(null, await sendErr(403, e)))
  if (payload) {
    const action = payload.headers.Action
    const handler = typeof handlers[action] !== 'undefined' ? handlers[action] : handlers.NOT_FOUND
    await handler.h(payload, async (_, res) => {
      if (res && res.s && res.o) {
        done(null, await protoResponse(res.s, res.o, handler.class))
      } else if (res && res.s && res.e) {
        done(null, await sendErr(res.s, res.e))
      }
    })
  } else {
    done(null, await sendErr(403, t('error.unauthorized')))
  }
}

export const response = promisify(_response)

import { promisify } from 'util'

import { DB_TYPE } from '../../config'
import db from './functions'
import { t } from '../translations'

const _joinedTableDelete = (table, col, done) => {
  const toDelete = col.length
  if (toDelete > 0) {
    let deleted = 0
    let errors = false
    col.forEach((id) => {
      db.destroy(table, id, (err) => {
        if (!err) {
          deleted += 1
        } else {
          errors = true
        }

        if (deleted === toDelete) {
          if (!errors) {
            done()
          } else {
            done(t('error.join_delete'))
          }
        }
      })
    })
  } else {
    done()
  }
}

export const joinedTableDelete = promisify(_joinedTableDelete)

export default DB_TYPE === 's3' ? db : {}

import { promisify } from 'util'

import s3 from '../s3'
const db = {}

const _create = async (dir, file, data, done) => {
  const res = await s3.save(`${dir}/${file}`, data).catch((e) => done(e))
  done(null, res)
}

const _read = async (dir, file, done) => {
  const res = await s3.getData(`${dir}/${file}`).catch((e) => done(e))
  done(null, res)
}

const _update = async (dir, file, newData, done) => {
  const res = await s3.getData(`${dir}/${file}`).catch((e) => done(e))
  if (res) {
    await s3.remove(`${dir}/${file}`).catch((e) => done(e))
    const res2 = await s3.save(`${dir}/${file}`, newData).catch((e) => done(e))
    done(null, res2)
  }
}

const _delete = async (dir, file, done) => {
  await s3.remove(`${dir}/${file}`).catch((e) => done(e))
  done()
}

const _list = async (dir, done) => {
  const res = await s3.listItems(dir).catch((e) => done(e))
  done(null, res)
}

const create = promisify(_create)
const read = promisify(_read)
const update = promisify(_update)
const destroy = promisify(_delete)
const listDir = promisify(_list)

db.create = create
db.read = read
db.update = update
db.destroy = destroy
db.listDir = listDir

export default db

import AWS from 'aws-sdk'
import { promisify } from 'util'

import { USERS_BUCKET_NAME } from '../../config'
import { md5 } from '../security'
const s3 = new AWS.S3()
const s3db = {}

const _getData = (id, done) => {
  const params = {
    Bucket: USERS_BUCKET_NAME,
    Key: id
  }

  s3.getObject(params, (err, data) => {
    if (err) {
      done(err.message)
    } else {
      const o = JSON.parse(data.Body.toString('utf8'))
      done(null, o)
    }
  })
}

const getData = promisify(_getData)

const _save = async (id, data, done) => {
  const s = typeof data === 'object' ? JSON.stringify(data) : data
  const params = {
    Body: Buffer.from(s),
    Bucket: USERS_BUCKET_NAME,
    ServerSideEncryption: 'AES256',
    StorageClass: 'STANDARD_IA',
    ContentMD5: md5(s),
    Key: id
  }
  s3.putObject(params, (err, data) => {
    if (err) {
      done(err.message)
    } else {
      done(null, JSON.parse(params.Body.toString()))
    }
  })
}

const save = promisify(_save)

const _remove = (id, done) => {
  const params = {
    Bucket: USERS_BUCKET_NAME,
    Key: id
  }

  s3.deleteObject(params, (err, data) => {
    if (err) {
      done(err.message)
    } else {
      done(null, data)
    }
  })
}

const remove = promisify(_remove)

const _multiRemove = (objs, done) => {
  const params = {
    Bucket: USERS_BUCKET_NAME,
    Delete: {
      Objects: objs,
      Quiet: false
    }
  }

  s3.deleteObjects(params, (err, data) => {
    if (err) {
      done(err.message)
    } else {
      done(null, data)
    }
  })
}

const multiRemove = promisify(_multiRemove)

const _list = (dir, done) => {
  const params = {
    Bucket: USERS_BUCKET_NAME,
    MaxKeys: 10,
    Prefix: dir
  }

  s3.listObjectsV2(params, (err, data) => {
    if (err) {
      done(err.message)
    } else {
      done(null, data.Contents)
    }
  })
}

const listItems = promisify(_list)

const _upload = (name, key, stream, done) => {
  const params = { Bucket: name, Key: key, Body: stream }
  s3.upload(params, (err, data) => {
    if (err) {
      done(err.message)
    } else {
      done(null, data)
    }
  })
}

const upload = promisify(_upload)

const _select = (bucket, key, query, done) => {
  // ex.: 'SELECT s.* FROM S3Object[*][*] s WHERE s.price < 9 LIMIT 10'
  const params = {
    Bucket: bucket,
    Key: key,
    ExpressionType: 'SQL',
    Expression: query,
    InputSerialization: {
      CompressionType: 'GZIP',
      JSON: {
        Type: 'DOCUMENT'
      }
    },
    OutputSerialization: {
      JSON: {
        RecordDelimiter: ','
      }
    }
  }
  s3.selectObjectContent(params)
    .promise()
    .then((output) => {
      output.Payload.on('data', (event) => {
        if (event.Records) {
          const buffer = event.Records.Payload
          done(null, buffer.toString())
        }
      })
    })
    .then((err) => {
      done(err)
    })
}

const select = promisify(_select)

s3db.getData = getData
s3db.save = save
s3db.remove = remove
s3db.multiRemove = multiRemove
s3db.listItems = listItems
s3db.upload = upload
s3db.select = select

export default s3db

import { inHTMLData } from 'xss-filters'
import { promisify } from 'util'
import { createHash, createHmac, createCipheriv, createDecipheriv, randomBytes } from 'crypto'

import db from '../db'
import { HASH_SECRET } from '../../config'
import { t } from '../translations'
const algorithm = 'aes-256-cbc'

const _random = (n, done) => {
  randomBytes(n, (err, buf) => {
    if (err) {
      done(err.message)
    }
    done(null, buf.toString('hex'))
  })
}

export const random = promisify(_random)

const _clean = (data, done) => {
  let isObject = false
  if (typeof data === 'object') {
    data = JSON.stringify(data)
    isObject = true
  }

  data = inHTMLData(data).trim()
  if (isObject) {
    data = JSON.parse(data)
  }

  done(null, data)
}

const clean = promisify(_clean)

export const xss = async (event) => {
  if (event.body) {
    event.body = await clean(event.body)
  }
  return event
}

const _encrypt = (data, key, iv, done) => {
  try {
    const cipher = createCipheriv(algorithm, Buffer.from(key, 'hex'), Buffer.from(iv, 'hex'))
    let encrypted = cipher.update(data, 'utf8', 'hex')
    encrypted += cipher.final('hex')
    done(null, encrypted)
  } catch (e) {
    done(e)
  }
}

const _decrypt = (encrypted, key, iv, done) => {
  try {
    const decipher = createDecipheriv(algorithm, Buffer.from(key, 'hex'), Buffer.from(iv, 'hex'))
    let decrypted = decipher.update(encrypted, 'hex', 'utf8')
    decrypted += decipher.final('utf8')
    done(null, decrypted)
  } catch (e) {
    done(e)
  }
}

export const md5 = (data) => {
  return createHash('md5').update(data).digest('base64')
}

export const encrypt = promisify(_encrypt)

export const decrypt = promisify(_decrypt)

export const uuidv4 = () => {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, (c) => {
    const r = Math.random() * 16 | 0
    const v = c === 'x' ? r : (r & 0x3 | 0x8)
    return v.toString(16)
  })
}

const _hash = (msg, done) => {
  if (typeof msg === 'string' && msg.length > 0) {
    done(null, createHmac('sha256', HASH_SECRET).update(msg).digest('hex'))
  } else {
    done(t('error.hashing'))
  }
}

export const hash = promisify(_hash)

const _randomID = (n, done) => {
  randomBytes(n, (err, buf) => {
    if (err) {
      done(t('error.bytes'))
    } else {
      done(null, buf.toString('hex'))
    }
  })
}

export const randomID = promisify(_randomID)

const _tokenHeader = (data, done) => {
  if (data.headers && typeof data.headers.Authorization === 'string') {
    let token = data.headers.Authorization.replace('Bearer ', '')
    token = token.length === 64 ? token : false
    done(null, token)
  } else {
    done(t('error.unauthorized'))
  }
}

export const tokenHeader = promisify(_tokenHeader)

const _auth = async (data, done) => {
  const token = await tokenHeader(data).catch((e) => done(e))
  if (token) {
    const tokenData = await db.read('tokens', token).catch(() => t('token.expired'))
    if (tokenData && tokenData.expiry > Date.now()) {
      done(null, tokenData)
    }
  }
  done(t('error.unauthorized'))
}

export const auth = promisify(_auth)

