            tosAgreement: u.tosAgreement,
            password: hashedPassword,
            referred: [],
            address: u.address,
            zipCode: u.zipCode,
            city: u.city,
            country: u.country ? countries.filter(i => i === u.country).country : '',
            dob: u.dob,
            avatarUrl: u.avatarUrl,
            confirmed: {
